						//搜索方法
						this.list.filter(item=>{
							//keywords为空时，显示全部列表
							if(item.name.includes(keywords) == true){
								newList.push(item)
							}
						})
						return newList
					},
			sort(n){
				this.list.sort((a,b) => {
					if(n === 1){  //升序
						return a.content - b.content;
					}if(n === 0){ //降序
						return b.content - a.content;
					}
				})
				if(n === 2){
					this.keywords = "";
					this.list.sort((a,b)=>{
						return a.id - b.id;
					})
					
